    private void jCBOperacionesDiagonalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jCBOperacionesDiagonalActionPerformed
        switch(jCBOperacionesDiagonal.getSelectedIndex()) {
            case 0:
            {
                salida += "La suma de la diagonarl principal es: " + matriz.sumarDiagonalPrincipal()+ "\n" ;
                this.jTAResultados.setText(salida);
                break;
            }
            case 1: 
            {
                salida += "La suma de la diagonarl secundaria es: "+ matriz.sumarDiagonalSecundaria()+ "\n";
                this.jTAResultados.setText(salida);
                break;
            }
            case 2: 
            {
              salida += "La suma de los Numeros pares es: " + matriz.sumarNumerosParesMatriz();
              this.jTAResultados.setText(salida);
              break;
            }
        }
    }//GEN-LAST:event_jCBOperacionesDiagonalActionPerformed

    private void jBAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBAceptarActionPerformed
        this.matriz = new Matriz(Integer.parseInt(jTFN.getText()), Integer.parseInt(jTFM.getText()));
        matriz.setMatriz();
        salida += "Matriz: \n" + matriz.toString();
        this.jTAResultados.setText(salida);
        //this.matriz = null;
        this.jTFN.setEnabled(false);
        this.jTFM.setEnabled(false); 
    }//GEN-LAST:event_jBAceptarActionPerformed

