    private void jBAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBAceptarActionPerformed
       this.matriz1 = new Matriz(Integer.parseInt(jTFN.getText()), Integer.parseInt(jTFM.getText()));
       this.matriz2 = new Matriz(Integer.parseInt(jTFN1.getText()), Integer.parseInt(jTFM1.getText()));
       this.matrizr = new Matriz(Integer.parseInt(jTFN1.getText()), Integer.parseInt(jTFN1.getText()));
       this.matrizpe = new Matriz(Integer.parseInt(jTFN1.getText()), Integer.parseInt(jTFN1.getText()));
       matriz1.setMatriz();
       matriz2.setMatriz();
       salida += "Matriz1 \n" + matriz1.toString();
       salida += "Matriz2 \n" + matriz2.toString();
       matrizpe = this.matriz2.productoEscalarMatriz(Integer.parseInt(this.jTFEscalar.getText()));
       this.matrizr=matriz1.productoMatriz(matrizpe);
       if ( matrizr == null) {
           salida += "No es posible Multiplicar";
       } else {
           salida += "Matriz Multiplicacion es: \n" + matrizr.toString();
       }
       this.jTAResultados.setText(salida);
       this.matriz1 = null;
       this.matriz2 = null;
       this.jTFN.setEnabled(false);
       this.jTFM.setEnabled(false);
       this.jTFN1.setEnabled(false);
       this.jTFM1.setEnabled(false);
       
       
    }//GEN-LAST:event_jBAceptarActionPerformed

