    private void jBAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBAceptarActionPerformed
        manejoStrings.setStrig(jTAEntrada.getText());
        manejoStrings.tokenizarString();
        salida+="Lista Original:\n"+manejoStrings.toString();
        manejoStrings.convertirMayusculaPrimeraLetra();
        salida+="\nLista Primera letra may√∫scula:\n"+manejoStrings.toString();
        manejoStrings.ordenarArrays();
        salida+="\nLista Ordenada:\n"+manejoStrings.toString();
        manejoStrings.convertirMayusculaPrimeraApellido();
        salida+="\nLista de Nombre completo:\n"+manejoStrings.toString();
        jTASalida.setText(salida);
        
    }//GEN-LAST:event_jBAceptarActionPerformed

